import { useState, useEffect } from "react";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  serverTimestamp,
  onSnapshot,
  updateDoc,
  doc,
  query,
  where,
} from "firebase/firestore";

export default function OutletDashboard() {
  const [form, setForm] = useState({ issueType: "", description: "" });
  const [tickets, setTickets] = useState([]);
  const outlet = localStorage.getItem("outlet");

  useEffect(() => {
    const q = query(collection(db, "tickets"), where("outlet", "==", outlet));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const data = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setTickets(data);
    });
    return () => unsubscribe();
  }, [outlet]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await addDoc(collection(db, "tickets"), {
      outlet,
      issueType: form.issueType,
      description: form.description,
      status: "ÿ¨ÿØŸäÿØÿ©",
      createdAt: serverTimestamp(),
    });
    setForm({ issueType: "", description: "" });
  };

  const updateStatus = async (id) => {
    await updateDoc(doc(db, "tickets", id), {
      status: "ÿ™ŸÖ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞",
      outletConfirmedAt: serverTimestamp(),
    });
  };

  const handleImageUpload = (ticketId) => {
    alert("üì∑ ÿ≥Ÿäÿ™ŸÖ ÿØÿπŸÖ ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ± ÿ®ÿπÿØ ÿ™ŸÅÿπŸäŸÑ Firebase Storage.");
  };

  const formatDate = (ts) =>
    ts?.toDate().toLocaleString("ar-EG", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    }) || "-";

  return (
    <div style={{ padding: "2rem", maxWidth: "1000px", margin: "auto" }}>
      <h2 style={{ textAlign: "center", color: "#f97316", marginBottom: "0.5rem" }}>
        ÿ™ÿ∞ÿßŸÉÿ± ÿßŸÑÿµŸäÿßŸÜÿ© ÿßŸÑÿÆÿßÿµÿ© ÿ®ŸÖÿπÿ±ÿ∂ŸÉ
      </h2>
      <p style={{ textAlign: "center", color: "#444", marginBottom: "2rem" }}>
        ÿßŸÑŸÖÿπÿ±ÿ∂ ÿßŸÑÿ≠ÿßŸÑŸä: <strong>{outlet}</strong>
      </p>

      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "column", gap: "1rem", marginBottom: "2rem" }}
      >
        <select
          name="issueType"
          value={form.issueType}
          onChange={handleChange}
          required
          style={{ padding: "0.5rem", fontSize: "1rem" }}
        >
          <option value="">ÿßÿÆÿ™ÿ± ŸÜŸàÿπ ÿßŸÑŸÖÿ¥ŸÉŸÑÿ©</option>
          <option value="ÿ£ÿπŸÖÿßŸÑ ÿ™ŸÉŸäŸäŸÅ">ÿ£ÿπŸÖÿßŸÑ ÿ™ŸÉŸäŸäŸÅ</option>
          <option value="ÿ£ÿπŸÖÿßŸÑ ÿ•ŸÜÿßÿ±ÿ©">ÿ£ÿπŸÖÿßŸÑ ÿ•ŸÜÿßÿ±ÿ©</option>
          <option value="ÿ£ÿπŸÖÿßŸÑ ÿ∑ŸÑÿßÿ°">ÿ£ÿπŸÖÿßŸÑ ÿ∑ŸÑÿßÿ°</option>
          <option value="ÿ£ÿπŸÖÿßŸÑ ÿÆÿ¥ÿ®">ÿ£ÿπŸÖÿßŸÑ ÿÆÿ¥ÿ®</option>
          <option value="ÿ£ÿÆÿ±Ÿâ">ÿ£ÿÆÿ±Ÿâ</option>
        </select>

        <textarea
          name="description"
          placeholder="ŸàÿµŸÅ ÿßŸÑŸÖÿ¥ŸÉŸÑÿ©"
          value={form.description}
          onChange={handleChange}
          required
          style={{ padding: "0.5rem", fontSize: "1rem", resize: "vertical", minHeight: "80px" }}
        />
        <button
          type="submit"
          style={{
            backgroundColor: "#f97316",
            color: "white",
            padding: "0.75rem",
            fontSize: "1rem",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          ÿ•ÿ±ÿ≥ÿßŸÑ ÿ™ÿ∞ŸÉÿ±ÿ© ÿ¨ÿØŸäÿØÿ©
        </button>
      </form>

      <table style={{ width: "100%", borderCollapse: "collapse", fontSize: "0.95rem" }}>
        <thead style={{ backgroundColor: "#f2f2f2" }}>
          <tr>
            <th style={thStyle}>ŸÜŸàÿπ ÿßŸÑŸÖÿ¥ŸÉŸÑÿ©</th>
            <th style={thStyle}>ÿßŸÑŸàÿµŸÅ</th>
            <th style={thStyle}>ÿßŸÑÿ≠ÿßŸÑÿ©</th>
            <th style={thStyle}>ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ•ŸÜÿ¥ÿßÿ°</th>
            <th style={thStyle}>ÿ®ÿØÿ£ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞</th>
            <th style={thStyle}>ÿ™ŸÖ ÿßŸÑÿ™ÿ£ŸÉŸäÿØ</th>
            <th style={thStyle}>ÿ•ÿ¨ÿ±ÿßÿ°</th>
          </tr>
        </thead>
        <tbody>
          {tickets.map((ticket) => (
            <tr key={ticket.id} style={{ textAlign: "center", borderTop: "1px solid #eee" }}>
              <td style={tdStyle}>{ticket.issueType}</td>
              <td style={tdStyle}>{ticket.description}</td>
              <td style={tdStyle}>{ticket.status}</td>
              <td style={tdStyle}>{formatDate(ticket.createdAt)}</td>
              <td style={tdStyle}>{formatDate(ticket.startedAt)}</td>
              <td style={tdStyle}>{formatDate(ticket.outletConfirmedAt)}</td>
              <td style={tdStyle}>
                {ticket.status === "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ŸÜŸÅŸäÿ∞" && (
                  <button
                    onClick={() => updateStatus(ticket.id)}
                    style={btnGreen}
                  >
                    ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞
                  </button>
                )}
                {ticket.status === "ÿ™ŸÖ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞" && (
                  <button
                    onClick={() => handleImageUpload(ticket.id)}
                    style={btnBlue}
                  >
                    ÿ•ÿ∂ÿßŸÅÿ© ÿµŸàÿ±
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

const thStyle = { padding: "10px", border: "1px solid #ddd", background: "#fafafa" };
const tdStyle = { padding: "8px", border: "1px solid #ddd" };
const btnGreen = {
  backgroundColor: "#16a34a",
  color: "white",
  padding: "0.5rem",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};
const btnBlue = {
  backgroundColor: "#3b82f6",
  color: "white",
  padding: "0.5rem",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};
